import { Component } from '@angular/core';
import {environment} from "../../../environments/environment";
import {HttpClient} from "@angular/common/http";
import { subscribe } from 'rxjs';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  inputValue: string = '';
  responseText: string = '';

  constructor(private http: HttpClient) {}

  showResponse() {
    const apiUrl = environment.apiUrl; // Use the environment variable

    this.http.get<any>(apiUrl).subscribe(
      (data) => {
        this.responseText = data.yourResponseProperty;
      },
      (error) => {
        console.error('API Error:', error);
      }
    );
  }
}

<div class="container">
  <div class="card">
    <div class="card-content">
      <h2>Input</h2>
      <input type="text" [(ngModel)]="inputValue" placeholder="Enter something...">
      <button (click)="showResponse()">Show Response</button>
    </div>
    <div class="card-response">
      <h2>Response</h2>
      <p>{{ responseText }}</p>
    </div>
  </div>
</div>
